mapper.select:
select a.*,b.paper_id, b.paper_name, b.paper_listening_file, b.paper_create_time, b.paper_question_num, b.paper_exam_time, b.paper_notice
from exam_paper b inner join exam_subject a on a.subject_id=b.subject_id
where b.paper_id=#{paper_id}

mapper.insert:
insert into exam_paper (paper_id, subject_id, paper_name, 
  paper_listening_file, paper_create_time, 
  paper_question_num, paper_exam_time, paper_notice
  )
values (#{paperId,jdbcType=VARCHAR}, #{subject.subject_id,jdbcType=VARCHAR}, #{paperName,jdbcType=VARCHAR}, 
  #{paperListeningFile,jdbcType=VARCHAR}, #{paperCreateTime,jdbcType=TIMESTAMP}, 
  #{paperQuestionNum,jdbcType=INTEGER}, #{paperExamTime,jdbcType=INTEGER}, #{paperNotice,jdbcType=VARCHAR}
  )
